mapper.select:
select
  fixed_id, fixed_name, fixed_location, fixed_create_time, fixed_owner, fixed_instalment, instalment_price, instalment_surplus, fixed_remark
from friday.user_assets


        and fixed_id = #{fixedId}


        and fixed_name = #{fixedName}


        and fixed_location = #{fixedLocation}


        and fixed_create_time = #{fixedCreateTime}


        and fixed_owner = #{fixedOwner}


        and fixed_instalment = #{fixedInstalment}


        and instalment_price = #{instalmentPrice}


        and instalment_surplus = #{instalmentSurplus}


        and fixed_remark = #{fixedRemark}
